import React from "react";
import { Grommet, Main, Text, Image, Box } from "grommet";
import image from "./image/IMG_9079.jpg";
import { PART_TASK, TASK } from "./Query";
import { useQuery } from "@apollo/client";

function Home() {
  //const { data } = useQuery(TASK);
  //const { data } = useQuery(PART_TASK, { variables: { id: 14 } });

  const { loading, error, data } = useQuery(PART_TAS, {
    variables: { id: "14" },
  });
  console.log(data);

  return (
    <Grommet>
      <Main>
        <Text>Wellcome!!!</Text>
      </Main>
      <Box height="medium" width="large">
        <Image fit="cover" src={image} />
      </Box>
    </Grommet>
  );
}

export default Home;

