import React, { useState } from 'react';
import { useMutation } from '@apollo/client';
import { CREATE_BOOKINFO, BOOKINFO } from './Query'

import {
  Box,
  Button,
  Grommet,
  Form,
  FormField,
  Heading,
  Layer,
  TextInput
} from "grommet";

import { Add, Close } from "grommet-icons";
import { grommet } from "grommet/themes";


const BookForm = () => {
  const [title, setTitle] = useState('')
  const [author, setAuthor] = useState('')

  const [open, setOpen] = React.useState(false);
  const onOpen = () => setOpen(true);
  const onClose = () => setOpen(undefined);


  const [ createBookinfo ] = useMutation(CREATE_BOOKINFO, {
      refetchQueries: [ { query: BOOKINFO } ]
  })


  const submit =  () => {

    createBookinfo({  variables: { title, author } })

    setTitle('')
    setAuthor('')
    }


  return (
    <Grommet >
      <Box fill align="center" justify="center">
        <Button  icon={<Add />} color="#00739D" label="Add" onClick={onOpen} margin={{ "bottom": "medium"}}/>
        {open && (
          <Layer
            position="left"
            full="vertical"
            modal
            onClickOutside={onClose}
            onEsc={onClose}
          >
            <Box
              as="form"
              fill="vertical"
              overflow="auto"
              width="medium"
              pad="medium"
              onSubmit={onClose}
            >
              <Box flex={false} direction="row" justify="between">
                <Heading level={2} margin="none">
                  AddBookinfo
                </Heading>
                <Button icon={<Close />} onClick={onClose} />
              </Box>
              <Box flex="grow" overflow="auto" pad={{ vertical: "medium" }}>
              <Form 
          onSubmit={({ value: nextValue }) => submit(nextValue)}>
          <FormField label="Title" name="title"  margin={"small",{"bottom":"small",}} required>
            <TextInput name="title" type="title" value={title}
            onChange={({ target }) => setTitle(target.value)}/>
          </FormField>

          <FormField label="Author" name="author" margin={"small",{"bottom":"small"}} required>
            <TextInput  name="author" type="author" value={author}
            onChange={({ target }) => setAuthor(target.value)}/>
          </FormField>

          <Box direction="row" justify="between" margin={{ top: "small" }}>
          </Box>
        </Form>
              </Box>
              <Box flex={false} as="footer" align="start">
                <Button
                  type="submit"
                  label="Add"
                  onClick={submit}
                  primary
                />
              </Box>
            </Box>
          </Layer>
        )}
      </Box>
    </Grommet>
  );
};
  export default BookForm

