import React from "react";
import { Grommet, Main, Text, Image, Box } from "grommet";
import image from "./image/IMG_9079.jpg";
import { PART_TASK, TASK } from "./Query";

function Home() {
  const [partTask] = useMutation(PART_TASK, {
    refetchQueries: [{ query: TASK }],
  });

  const state = partTask({ variables: { id: 14 } });

  console.log(state);

  return (
    <Grommet>
      <Main>
        <Text>Wellcome!!!</Text>
      </Main>
      <Box height="medium" width="large">
        <Image fit="cover" src={image} />
      </Box>
    </Grommet>
  );
}

export default Home;

