import React, { useState } from 'react';
import { useMutation } from '@apollo/client';
import { EDIT_BOOKINFO, BOOKINFO } from './Query';

import switchFlag from './testedit'
import Selector from './testedit';

function EditForm() {
//const EditForm = () => {
  const [id, setId] = useState('');
  const [title, setTitle] = useState('');
  const [author, setAuthor] = useState('');
  const [updateBookinfo] = useMutation(EDIT_BOOKINFO, {
    refetchQueries: [{ query: BOOKINFO }]
  });

  const submit = async (event) => {
    event.preventDefault();
    updateBookinfo({ variables: { id, title, author } });
    setId('');
    setTitle('');
    setAuthor('');
  };
  //
  const Click = () => {
     Selector.Click()
  }
 //
  return (<div>
    <form onSubmit={submit}>
      <div>
        id <input value={id} onChange={({ target }) => setId(target.value)} />
      </div>
      <div>
        title <input value={title} onChange={({ target }) => setTitle(target.value)} />
      </div>
      <div>
        author <input value={author} onChange={({ target }) => setAuthor(target.value)} />
      </div>
      <button type='submit' onClick={Click}>Edit!!</button>
    </form>
  </div>);
}

export default EditForm
