import React from "react";
import {
  Grommet,
  Box,
  Heading,
  Grid,
  Table,
  TableBody,
  TableCell,
  TableFooter,
  TableHeader,
  TableRow,
  Text,
  CheckBox,
  List,
  Button,
  FormField,
  Form,
  RadioButtonGroup,
} from "grommet";

import FormLayer from "./CreateTodo";

import { useQuery, useMutation } from "@apollo/client";
import { TASK, DELETE_TASK } from "./Query";

const COLUMNS = [
  {
    property: "task",
    label: "TASK",
    format: (task) => <strong>{task.task}</strong>,
  },
  {
    property: "limit",
    label: "LIMIT",
  },
];

function TodoList() {
  const { loading, error, data } = useQuery(TASK);
  const [checked, setChecked] = React.useState(true);

  const [deleteTask] = useMutation(DELETE_TASK, {
    refetchQueries: [{ query: TASK }],
  });

  const deletetask = (id) => {
    deleteTask({ variables: { id } });
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;

  return (
    <Grommet>
      <Box fill align="center" justify="center">
        <List primaryKey="task" secondaryKey="limit" data={data.tasks} />
      </Box>
    </Grommet>
  );
}

