import React, { useState, useEffect } from "react";
import { Box, Grommet, CheckBox, Button } from "grommet";
import { EDIT_TASK_COMPLETED, TASK, DELETE_TASK, PART_TASK } from "./Query";
import { useQuery } from "@apollo/client";
import { Trash } from "grommet-icons";

function Todotest() {
  const { loading, error, data } = useQuery(TASK);
  const [checked, setChecked] = useState(false);
  //const [isBtnHide, setIsBtnHide] = useState(true);

  const onChange = () => {
    setChecked(!checked);
    console.log(checked);
  };

  const deletebutton =
    checked === true ? (
      <Button plain={false} icon={<Trash size="small" />} margin="small" />
    ) : null;

  //////////

  //checkedItemsは初期値を空のオブジェクトにする
  const [checkedItems, setCheckedItems] = useState({});
  //ひとつでもcheckedになっている場合にのみ送信ボタンを表示させたいので、全体のStateを監視する
  const [isBtnHide, setIsBtnHide] = useState(true);

  //const { loading, error, data } = useQuery(TASK);

  useEffect(() => {
    //checkedItemsが空では無い場合、送信ボタンを表示させる
    Object.keys(checkedItems).length && setIsBtnHide(false);
    //すべてのcheckedItemの値がfalseの場合に送信ボタンを表示させる
    setTimeout(() => {
      if (
        Object.values(checkedItems).every((checkedItem) => {
          return checkedItem === false;
        })
      ) {
        setIsBtnHide(true);
      }
    }, 100);
  }, [checkedItems]);

  const handleChange = (e) => {
    //checkedItemsのstateをセット
    setCheckedItems({
      ...checkedItems,
      [e.target.id]: e.target.checked,
    });
    console.log("checkedItems:", checkedItems);
  };

  const dataSendBtn = (e) => {
    //既定のイベントをキャンセルさせる
    e.preventDefault();
    //送信ボタンを押したタイミングで、checkedItemsオブジェクトのvalueがtrueのkeyのみを配列にしてconsoleに表示させる
    const dataPushArray = Object.entries(checkedItems).reduce(
      (pre, [key, value]) => {
        value && pre.push(key);
        return pre;
      },
      []
    );
    console.log("dataPushArray:", dataPushArray);
  };
  //////////

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;

  return (
    <Grommet>
      <p>Todo</p>
      <Box>
        {data.tasks.map((task) => {
          return (
            <CheckBox
              key={task.id}
              checked={checked[task.id]}
              label={task.task}
              onChange={onChange}
            />
          );
        })}
        {/* checkedがない場合には送信ボタンを表示させない */}
        {!isBtnHide && <button>アンケート送信ボタン</button>}
      </Box>
      <Box>{deletebutton}</Box>
    </Grommet>
  );
}

export default Todotest;

