import React from "react";

import TodoList from "./Todolist";
import CreateTodo from "./CreateTodo";

import { Grommet, Heading, Box, Grid, Button } from "grommet";
import { Trash } from "grommet-icons";

function MyTodolist() {
  return (
    <Grommet>
      <Grid fill rows={["auto", "flex", "auto"]}>
        <Box
          align="center"
          tag="header"
          pad="medium"
          color="#00739D"
          width="large"
        >
          <Heading margin="none" color="#00739D" size="small">
            My TodoList
          </Heading>
        </Box>

        <TodoList />
        <CreateTodo />
        <Button plain={false} icon={<Trash size="small" />} margin="small" />
      </Grid>
    </Grommet>
  );
}

export default MyTodolist;
