import React from "react";
import { Grommet, Main, Text, Image, Box } from "grommet";
import image from "./image/IMG_9079.jpg";
import { useQuery, useMutation } from "@apollo/client";
import { TASK, EDIT_TASK_COMPLETED } from "./Query";

function Home() {
  const { data } = useQuery(TASK);

  const [updateTaskCompleted] = useMutation(EDIT_TASK_COMPLETED, {
    refetchQueries: [{ query: TASK }],
  });

  updateTaskCompleted({ variables: { id: 14, completed: false } });

  console.log(data);
  return (
    <Grommet>
      <Main>
        <Text>Wellcome!!!</Text>
      </Main>
      <Box height="medium" width="large">
        <Image fit="cover" src={image} />
      </Box>
    </Grommet>
  );
}

export default Home;
