import React from 'react';
import { useQuery, useMutation } from '@apollo/client';
import { DELETE_BOOKINFO, BOOKINFO } from './Query'
import Selector from './selectbutton'

import classes from "./Table.module.css";


function Books () {
    const { loading, error, data } = useQuery(BOOKINFO);

    const [ deleteBookinfo ] = useMutation(DELETE_BOOKINFO, {
        refetchQueries: [ { query: BOOKINFO } ]
    })


    const deleteInfo = (id) => {
        deleteBookinfo({ variables: { id } })
    }

    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error :(</p>;

        return (
          <div>
          <table className="Table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Title</th>
              <th>Author</th>
              <th>Location</th>
            </tr>
          </thead>
          <tbody>
            {data.bookinfos.map(book => (
                        <tr  key={book.id}>ã€€
                            <td>{book.id}</td>
                            <td>{book.title}</td>
                            <td>{book.author}</td>
                            <button onClick={() => deleteInfo(book.id)} >Delete</button>
                            <Selector/>
                        </tr>

                      ))}
          </tbody>
        </table>
        </div>
          );
  }

export default Books;
