import React from 'react';
import { useQuery, useMutation } from '@apollo/client';
import { DELETE_BOOKINFO, BOOKINFO } from './Query'
import {Grommet, Text, Table, TableHeader, TableFooter, TableBody, TableRow, TableCell } from 'grommet';



function Test () {
  const { data } = useQuery(BOOKINFO);

  let TOTAL = 0;
  DATA.forEach((datum) => { TOTAL += datum.amount; });

  const amountFormatter = new Intl.NumberFormat('en-US', {
    style: 'currency',
    currency: 'USD',
    minimumFractionDigits: 2,
  });


  const COLUMNS = [
    {
      property: 'id',
      label: 'Id',
      format: datum => <strong>{datum.id}</strong>,
    },
    {
      property: 'title',
      label: 'Title',
    },
    {
      property: 'author',
      label: 'Author',
      },
  ];

  return (
        <Grommet>
            <Table>
      <TableHeader>
        <TableRow>
          {COLUMNS.map(c => (
            <TableCell key={c.property}>
              <Text>{c.label}</Text>
            </TableCell>
          ))}

        </TableRow>
      </TableHeader>
      <TableBody>
        {data.bookinfos.map(book => (
          <TableRow key={book.id}>
            {COLUMNS.map(c => (
              <TableCell key={c.property}>
                <Text>
                  {c.format ? c.format(datum) : datum[c.property]}
                </Text>
              </TableCell>
            ))}
          </TableRow>
        ))}
      </TableBody>
    </Table>
    </Grommet>
        );
}

export default Test;
