import React, {useState}from 'react'
import EditForm from './EditBookinfo'
import Editbutton from './Editbutton'


function Edit(props) {
  //const isButton = props.isButton;
const [edit, setEdit] = useState(true)

  if (edit) {
    return <Editbutton setEdit={setEdit}/>;
  }
  return <EditForm />;
}

export default Edit
