import { gql } from "@apollo/client";

// ALLBOOKINFO //
export const BOOKINFO = gql`
  {
    bookinfos {
      id
      title
      author
    }
  }
`;
// CREATE NEW BOOKINFO //
export const CREATE_BOOKINFO = gql`
  mutation CreateBookinfo($title: String!, $author: String!) {
    createBookinfo(input: { title: $title, author: $author }) {
      bookinfo {
        id
        title
        author
      }
    }
  }
`;

// EDIT BOOKINFO //
export const EDIT_BOOKINFO = gql`
  mutation UpdateBookinfo($id: ID!, $title: String!, $author: String!) {
    updateBookinfo(input: { id: $id, title: $title, author: $author }) {
      bookinfo {
        id
        title
        author
      }
    }
  }
`;
// DELETE BOOKINFO //
export const DELETE_BOOKINFO = gql`
  mutation DeleteBookinfo($id: ID!) {
    deleteBookinfo(input: { id: $id }) {
      bookinfo {
        id
        title
        author
      }
    }
  }
`;

// ALL TASK //
export const TASK = gql`
  {
    tasks {
      id
      task
      limit
    }
  }
`;

// CREATE NEW TASK //
export const CREATE_TASK = gql`
  mutation CreateTask($task: String!, $limit: ISO8601Date) {
    createTask(input: { task: $task, limit: $limit }) {
      task {
        id
        task
        limit
      }
    }
  }
`;

// DELETE TASK //
export const DELETE_TASK = gql`
  mutation DeleteTask($id: ID!) {
    deleteTask(input: { id: $id }) {
      task {
        id
        task
        limit
      }
    }
  }
`;
