import React from "react";
import { Grommet, Main, Text, Image, Box } from "grommet";
import image from "./image/IMG_9079.jpg";

import { gql, useQuery, useMutation } from "@apollo/client";

const TASK = gql`
  query tasks {
    tasks {
      id
      task
      limit
      completed
    }
  }
`;

const PART_TASK = gql`
  query gettask($id: ID) {
    task(id: $id) {
      id
      completed
    }
  }
`;

const EDIT_TASK_COMPLETED = gql`
  mutation UpdateTaskCompleted($id: ID!, $completed: Boolean) {
    updateTaskCompleted(input: { id: $id, completed: $completed }) {
      task {
        id
        completed
      }
    }
  }
`;

function Home() {
  // const { loading, error, data } = useQuery(TASK);
  /*
  const [pastTask] = useMutation(EDIT_TASK_COMPLETED, {
    refetchQueries: [{ query: TASK }],
  });

  pastTask({ variables: { id: 14, completed: true } });
*/

  const { loading, error, data } = useQuery(PART_TASK, {
    variables: { id: 14 },
  });

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;
  console.log(data);

  return (
    <Grommet>
      <Main>
        <Text>Wellcome!!!</Text>
      </Main>
      <Box height="medium" width="large">
        <Image fit="cover" src={image} />
      </Box>
    </Grommet>
  );
}

export default Home;

