import React from "react";
import { Grommet, Box, List, Heading, Grid } from "grommet";
import { useQuery, useMutation } from "@apollo/client";
import { TASK, DELETE_TASK } from "./Query";

function TodoList() {
  const { loading, error, data } = useQuery(TASK);

  const [deleteTask] = useMutation(DELETE_TASK, {
    refetchQueries: [{ query: TASK }],
  });
  const deletetask = (id) => {
    deleteTask({ variables: { id } });
  };

  console.log(data.tasks);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;

  return (
    <Grommet>
      <Grid fill rows={["auto", "flex", "auto"]}>
        <Box align="center" tag="header" pad="medium" color="#00739D">
          <Heading margin="none" color="#00739D" size="small">
            My TodoList
          </Heading>
        </Box>
        <Box
          overflow={{ vertical: "scroll", horizontal: "auto" }}
          height="medium"
          width="medium"
        >
          <List primaryKey="task" secondaryKey="limit" data={data.tasks} />
        </Box>
      </Grid>
    </Grommet>
  );
}

