import React, { useState } from 'react';
import { useMutation } from '@apollo/client';
import { CREATE_BOOKINFO, BOOKINFO } from './Query'

import {
  Box,
  Button,
  Grommet,
  Form,
  FormField,
  Text,
  TextInput
} from "grommet";
import { grommet } from "grommet/themes";

// This example shows a way to perform validation across multiple fields.
const Example = () => {
  const [title, setTitle] = useState('')
  const [author, setAuthor] = useState('')


  const [ createBookinfo ] = useMutation(CREATE_BOOKINFO, {
      refetchQueries: [ { query: BOOKINFO } ]
  })


  const submit =  () => {

    createBookinfo({  variables: { title, author } })

    setTitle('')
    setAuthor('')
    }


  return (
    <Grommet >
      <Box fill align="center" justify="center">
        <Box width="mudium">
          <Form
            onSubmit={({ value: nextValue }) => submit(nextValue)}

          >
            <FormField label="Title" name="title" required>
              <TextInput name="title" type="title" value={title}
              onChange={({ target }) => setTitle(target.value)}/>
            </FormField>

            <FormField margin= {{ "bottom": "small", "vertical": "xsmall", }} label="Author" name="author" required>
              <TextInput  name="author" type="author" value={author}
              onChange={({ target }) => setAuthor(target.value)}/>
            </FormField>

            <Box direction="row" justify="between" margin={{ top: "small" }}>

              <Button size="small" type="submit" label="Add" primary />
            </Box>
          </Form>
        </Box>
      </Box>
    </Grommet>
  );
};

export default Example;
