import React from "react";
import { Grommet, Box, List } from "grommet";
import { gql, useQuery } from "@apollo/client";

const TASK = gql`
  {
    tasks {
      id
      task
      limit
    }
  }
`;

const TodoList = () => {
  const data = useQuery(TASK);

  return <div>{data.tasks.map((p) => p.name).join(", ")}</div>;
};

