import React from "react";
import { useQuery, useMutation } from "@apollo/client";
import { DELETE_TASK, TASK } from "./Query";
import { Grommet, Button, Box } from "grommet";

function Todolist() {
  const { loading, error, data } = useQuery(TASK);

  const [deleteTask] = useMutation(DELETE_TASK, {
    refetchQueries: [{ query: TASK }],
  });

  const deleteTasks = (id) => {
    deleteTask({ variables: { id } });
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;

  return (
    <Grommet>
      <Box>
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Task</th>
              <th>Limit</th>
            </tr>
          </thead>
          <tbody>
            {data.tasks.map((task) => (
              <tr key={task.id}>
                <td>{task.id}</td>
                <td>{task.task}</td>
                <td>{task.limit}</td>
                <td>
                  <button onClick={() => deleteTasks(task.id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </Box>
    </Grommet>
  );
}
