import React from 'react';
import { useQuery, useMutation } from '@apollo/client';
import { DELETE_BOOKINFO, BOOKINFO } from './Query'
import {Grommet, Text, Table, TableHeader, TableFooter, TableBody, TableRow, TableCell } from 'grommet';



function Test () {
  const { data } = useQuery(BOOKINFO);



  const COLUMNS = [
    {
      property: 'id',
      label: 'Id',
      format: datum => <strong>{datum.id}</strong>,
    },
    {
      property: 'title',
      label: 'Title',
    },
    {
      property: 'author',
      label: 'Author',
      },
  ];

  return (
        <Grommet>
            <Table>
      <TableHeader>
        <TableRow>
          {COLUMNS.map(c => (
            <TableCell key={c.property}>
              <Text>{c.label}</Text>
            </TableCell>
          ))}

        </TableRow>
      </TableHeader>

    </Table>
    </Grommet>
        );
}

export default Test;
