import React from 'react';
import EditForm from './EditBookinfo';
import Books from './t'

class Selector extends React.Component{
  state = { Component: null }

  selectBooks = () => this.setState({Component: Books})
  selectEditForm = () => this.setState({Component: EditForm})

  render() {
    // 大文字始まりにしないと、JSXでコンポーネントと認識させられない
    const {Component} = this.state;
    if(Component) return <Component />;
    return(
      <div>
        <div className="intro">Select type of Game!</div>
        <button type="button" className="square_btn" name="aaa" value="aaa" onClick={this.selectBooks}>to GAME1</button>
        <button type="button" className="square_btn" name="bbb" value="bbb" onClick={this.selectEditForm}>to GAME2</button>
      </div>
    );
  }
}

// 使う箇所では<Selector />をrenderする
