import React, { useState }  from 'react';
import EditForm from './EditBookinfo';
import Books from './t'

/*
class Selector extends React.Component{
  state = { Component: null }

  selectBooks = () => this.setState({Component: Books})
  selectEditForm = () => this.setState({Component: EditForm})

  render() {

    const {Component} = this.state;
    if(Component) return <Component />;
    return(
      <div>
        <div className="intro">Select Menu!</div>
        <button type="button" className="square_btn" name="aaa" value="aaa" onClick={this.selectBooks}>Bookinfo</button>
        <button type="button" className="square_btn" name="bbb" value="bbb" onClick={this.selectEditForm}>Edit</button>
      </div>
    );
  }
}
*/

const Selector = () => {
  const [component, setComponent] = useState(null)
  const handleOrangeClick = () => {
    setComponent(EditForm)
  }
  const {Component} = {component}
　if(Component) return <Component />
  return (
    <div>
      <button type="button"　onClick={() => handleOrangeClick}>Edit</button>
    </div>
  )
}

export default Selector
