import React from 'react';
import './App.css';
import Books from './Components/Bookinfos'
import BookForm from './Components/CreateBook'
import { Grommet, Heading,Box,grommet,Grid } from 'grommet'


import { Admin } from 'react-admin';
import simpleRestProvider from 'ra-data-simple-rest';

import Dashboard from './Dashboard';


class App extends React.Component {
  render() {
    return (

      <Admin dashboard={Dashboard} dataProvider={simpleRestProvider('http://localhost:3000/')}>
      <Grommet theme={grommet} full >
      <Grid fill rows={["auto", "flex", "auto"]}>
      <Box align="center" tag="header" background="#00739D" pad="medium" color="#00739D">
      <Heading margin="none" color="#F2F2F2">My BookInfo</Heading>
      </Box>


      <Books/>   
       <BookForm/> 


        <Box flex={false} tag="footer" background="#00739D" pad="medium">
      <Heading margin="none" size="small"></Heading>
      </Box>
      </Grid>
    </Grommet>
    </Admin>
    );
  }

}


