import React , { useState } from 'react';
import { useQuery, gql, useMutation,  } from '@apollo/client';


// ALLBOOKINFO //
export const BOOKINFO = gql`
{
bookinfos {
  id
  title 
  author
}
}
`
// DELETE BOOKINFO //
export const DELETE_BOOKINFO = gql `
mutation DeleteBookinfo($id: ID!){
  deleteBookinfo(
    input:{
      id: $id
    }){
    bookinfo {
      id
      title
      author
    }
  }
}
`
/////



function Books () {
    const { loading, error, data } = useQuery(BOOKINFO);

    const [ deleteBookinfo ] = useMutation(DELETE_BOOKINFO, {
        refetchQueries: [ { query: BOOKINFO } ]
    })


    const deleteInfo = (id) => {
        deleteBookinfo({ variables: { id } })
    }


    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error :(</p>;

        return (
            <div>
              {data.bookinfos.map(book => (
                <li key={book.id}>ã€€
                    <p>{book.id}</p>,
                    <p>{book.title}</p>,
                    <p>{book.author}</p>,
                    <button>Edit</button>,
                    <button onClick={() => deleteInfo(book.id)} >Delete</button>
                </li>
              ))}
            </div>
          );
  }

export default Books;
