import React from 'react';
import EditForm from './EditBookinfo';
import DeleteForm from './DeleteBookinfo'

//function Selector() {
class Selector extends React.Component{
  state = { Component: null }

  selectDeleteForm = () => this.setState({Component: DeleteForm})
  selectEditForm = () => this.setState({Component: EditForm})

  render() {

    const {Component} = this.state;
    if(Component) return <Component />;
    return(
      <div>


        <button type="button" className="square_btn" name="bbb" value="bbb" onClick={this.selectEditForm}>Edit</button>
      </div>
    );
  }
}

export default Selector
