import React from "react";


import { Grommet, Box, DataTable } from "grommet";
import { grommet } from "grommet/themes";

import { useQuery } from '@apollo/client';
import { BOOKINFO } from './Query'


const COLUMNS = [
  {
    property: 'id',
    header: 'ID'
  },
  {
    property: 'title',
    header: 'Title',
    dataScope: 'row',
    format: book => <strong>{book.title}</strong>,
  },
  {
    property: 'author',
    label: 'Author',
  },

];

function DataTabletest() {
  const { data } = useQuery(BOOKINFO)

return(
  <Grommet theme={grommet}>
    <Box align="center" pad="large">
      <DataTable columns={COLUMNS} data={data} size="medium" />
    </Box>
  </Grommet>
)}

export default DataTabletest
