import React, { useState, Component } from "react";
import { Grommet, Box, Button, Grid, Text, Avatar } from "grommet";
import { grommet } from "grommet/themes";
import { Favorite } from "grommet-icons";

import MyBookinfo from './Components/Test_dash'



import { BrowserRouter as Router, Route } from 'react-router-dom';
import Navbar from './Components/Router';


const AppGrid = () => {
  const [sidebar, setSidebar] = useState(true);

  return (
    <Grommet full theme={grommet}>
      <Grid
        fill
        rows={["auto", "flex"]}
        columns={["auto", "flex"]}
        areas={[
          { name: "header", start: [0, 0], end: [1, 0] },
          { name: "sidebar", start: [0, 1], end: [0, 1] },
          { name: "main", start: [1, 1], end: [1, 1] }
        ]}
      >
        <Box
          gridArea="header"
          direction="row"
          align="center"
          justify="between"
          pad={{ horizontal: "medium", vertical: "small" }}
          background="#00739D"
        >
          <Button onClick={() => setSidebar(!sidebar)}>
            <Text size="large">Nahokov Web</Text>
          </Button>
          <Avatar background="light-2">
          <Favorite color="accent-3" />
        </Avatar>
        </Box>
        {sidebar && (
          <Box
            gridArea="sidebar"
            background="dark-3"
            width="small"
            animation={[
              { type: "fadeIn", duration: 300 },
              { type: "slideRight", size: "xlarge", duration: 150 }
            ]}
          >

            <Button href="<Route path='/books' component={MyBookinfo} />" hoverIndicator>
                <Box pad={{ horizontal: "medium", vertical: "small" }}>
                <Text>Books</Text>
                </Box>
              </Button>



          </Box>
        )}
        <Box gridArea="main" justify="center" align="center">
          <MyBookinfo/>
         </Box>
      </Grid>
    </Grommet>
  );
};
export default AppGrid

