import React, { useState, useEffect } from "react";
import { TASK, DELETE_TASK } from "./Query";
import { useQuery, useMutation } from "@apollo/client";
import { Trash } from "grommet-icons";
import {
  Table,
  TableBody,
  TableCell,
  TableHeader,
  TableRow,
  Text,
  Grommet,
  Button,
  Box,
  CheckBox,
} from "grommet";

const COLUMNS = [
  {
    property: "task",
    label: "Task",
    format: (task) => <strong>{task.task}</strong>,
  },
  {
    property: "limit",
    label: "Limit",
  },
];

function TodoList2() {
  const { loading, error, data } = useQuery(TASK);

  const [deleteTask] = useMutation(DELETE_TASK, {
    refetchQueries: [{ query: TASK }],
  });

  const deleteTasks = (id) => {
    deleteTask({ variables: { id } });
  };

  //checkedItemsは初期値を空のオブジェクトにする
  const [checkedItems, setCheckedItems] = useState({});
  //ひとつでもcheckedになっている場合にのみ送信ボタンを表示させたいので、全体のStateを監視する
  const [isBtnHide, setIsBtnHide] = useState(true);

  //const { loading, error, data } = useQuery(TASK);

  useEffect(() => {
    //checkedItemsが空では無い場合、送信ボタンを表示させる
    Object.keys(checkedItems).length && setIsBtnHide(false);
    //すべてのcheckedItemの値がfalseの場合に送信ボタンを表示させる
    setTimeout(() => {
      if (
        Object.values(checkedItems).every((checkedItem) => {
          return checkedItem === false;
        })
      ) {
        setIsBtnHide(true);
      }
    }, 100);
  }, [checkedItems]);

  const handleChange = (e) => {
    //checkedItemsのstateをセット
    setCheckedItems({
      ...checkedItems,
      [e.target.id]: e.target.checked,
    });
    //console.log("checkedItems:", checkedItems);
  };

  const deletetask = () => {
    const dataPushArray = Object.entries(checkedItems);

    //dataPushArray.map((item) => {
    //  deleteTasks(item[0]);
    //});
    dataPushArray.forEach((item) => {
      deleteTasks(item[0]);
    });
    //console.log(checkedItems);
    setCheckedItems({});
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;

  return (
    <Grommet>
      <Box fill align="center" justify="center">
        <Box
          overflow={{ vertical: "scroll", horizontal: "auto" }}
          height="medium"
        >
          <Table sortable="true">
            <TableHeader>
              <TableRow>
                {COLUMNS.map((c) => (
                  <TableCell
                    key={c.property}
                    scope="col"
                    border="bottom"
                    align={c.align}
                  >
                    <Text>{c.label}</Text>
                  </TableCell>
                ))}
              </TableRow>
            </TableHeader>
            <TableBody>
              {data.tasks.map((task) => (
                <TableRow key={task.id}>
                  {COLUMNS.map((c) => (
                    <TableCell
                      margin="small"
                      scope="col"
                      border="bottom"
                      key={c.property}
                      align={c.align}
                    >
                      <Text>
                        {c.format ? c.format(task) : task[c.property]}
                      </Text>
                    </TableCell>
                  ))}
                  <TableCell>
                    <CheckBox
                      id={task.id}
                      value={task}
                      checked={checkedItems[task.id]}
                      onChange={handleChange}
                    />
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </Box>
        <Box>
          {!isBtnHide && (
            <Button
              plain={false}
              icon={<Trash size="small" />}
              label="Delete"
              margin="small"
              onClick={deletetask}
            />
          )}
        </Box>
      </Box>
    </Grommet>
  );
}

export default TodoList2;
