import React from "react";
import {
  Grommet,
  Box,
  Heading,
  Grid,
  Table,
  TableBody,
  TableCell,
  TableFooter,
  TableHeader,
  TableRow,
  Text,
  CheckBox,
  List,
  Button,
  FormField,
  Form,
  RadioButtonGroup,
} from "grommet";

import { useQuery, useMutation } from "@apollo/client";
import { TASK, DELETE_TASK } from "./Query";

const COLUMNS = [
  {
    property: "task",
    label: "TASK",
    format: (task) => <strong>{task.task}</strong>,
  },
  {
    property: "limit",
    label: "LIMIT",
  },
];

function TodoList() {
  const { loading, error, data } = useQuery(TASK);
  const [checked, setChecked] = React.useState(true);

  const [deleteTask] = useMutation(DELETE_TASK, {
    refetchQueries: [{ query: TASK }],
  });

  const deletetask = (id) => {
    deleteTask({ variables: { id } });
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;

  return (
    <Grommet>
      <Grid fill rows={["auto", "flex", "auto"]}>
        <Box align="center" tag="header" pad="medium" color="#00739D">
          <Heading margin="none" color="#00739D" size="small">
            My TodoList
          </Heading>
        </Box>
        <Box fill align="center" justify="center">
          <Box
            overflow={{ vertical: "scroll", horizontal: "auto" }}
            height="medium"
          >
            <Table sortable="true">
              <TableHeader border="bottom">
                <TableRow>
                  {COLUMNS.map((c) => (
                    <TableCell
                      key={c.property}
                      scope="col"
                      border="bottom"
                      align={c.align}
                    >
                      <Text>{c.label}</Text>
                    </TableCell>
                  ))}
                </TableRow>
              </TableHeader>
              <TableBody>
                {data.tasks.map((task) => (
                  <TableRow key={task.id}>
                    {COLUMNS.map((c) => (
                      <TableCell
                        scope="col"
                        border="bottom"
                        key={c.property}
                        align={c.align}
                      >
                        <Text>
                          {c.format ? c.format(task) : task[c.property]}
                          <CheckBox />
                        </Text>
                      </TableCell>
                    ))}
                    <TableCell alignSelf="center" border={"top"}>
                      <CheckBox
                        checked={checked}
                        onChange={(event) => setChecked(event.target.checked)}
                      />
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </Box>
        </Box>
      </Grid>
    </Grommet>
  );
}

