import React, { useState } from 'react';
import { useMutation } from '@apollo/client';
import { CREATE_BOOKINFO, BOOKINFO } from './Query'
import { Button } from 'grommet';

const BookForm = () => {
    const [title, setTitle] = useState('')
    const [author, setAuthor] = useState('')


    const [ createBookinfo ] = useMutation(CREATE_BOOKINFO, {
        refetchQueries: [ { query: BOOKINFO } ]
    })


    const submit =  (event) => {
      event.preventDefault()

      createBookinfo({  variables: { title, author } })

      setTitle('')
      setAuthor('')
      }

    return (
      <div>
        <h2>Create new</h2>
        <form onSubmit={submit}>
          <div>
            title <input value={title}
              onChange={({ target }) => setTitle(target.value)}
            />
          </div>
          <div>
          author <input value={author}
              onChange={({ target }) => setAuthor(target.value)}
            />
          </div>


          <Button type='submit' margin="xsmall" size="xsmall" label='Add'/>
        </form>
      </div>
    )
  }

  export default BookForm
