import React, { useState } from "react";
import { Box, Grommet, CheckBox } from "grommet";
import { EDIT_TASK_COMPLETED, TASK, DELETE_TASK, PART_TASK } from "./Query";
import { useQuery } from "@apollo/client";

function Todotest() {
  const { loading, error, data } = useQuery(TASK);
  const [checked, setChecked] = useState(true);

  const onChange = (e) => {
    setChecked(!checked);
    console.log(checked);
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;

  return (
    <Grommet>
      <Box>
        {data.tasks.map((task) => (
          <CheckBox
            key={task.id}
            checked={checked}
            label={task.task}
            onChange={(key) => setChecked(key.checked)}
          />
        ))}
      </Box>
    </Grommet>
  );
}

export default Todotest;
