import React from "react";
import {
  Grommet,
  Box,
  List,
  Heading,
  Grid,
  Table,
  TableBody,
  TableCell,
  TableFooter,
  TableHeader,
  TableRow,
  Text,
  Button,
} from "grommet";
import { Add, Close, Edit } from "grommet-icons";
import { useQuery, useMutation } from "@apollo/client";
import { TASK, DELETE_TASK } from "./Query";

const COLUMNS = [
  {
    property: "task",
    label: "TASK",
    format: (task) => <strong>{task.task}</strong>,
  },
  {
    property: "limit",
    label: "LIMIT",
  },
];

function TodoList() {
  const { loading, error, data } = useQuery(TASK);

  const [deleteTask] = useMutation(DELETE_TASK, {
    refetchQueries: [{ query: TASK }],
  });
  const deletetask = (id) => {
    deleteTask({ variables: { id } });
  };

  console.log(data);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;

  return (
    <Grommet>
      <Grid fill rows={["auto", "flex", "auto"]}>
        <Box align="center" tag="header" pad="medium" color="#00739D">
          <Heading margin="none" color="#00739D" size="small">
            My TodoList
          </Heading>
        </Box>
        <Box
          overflow={{ vertical: "scroll", horizontal: "auto" }}
          height="medium"
          width="medium"
        >
          <Table sortable="true">
            <TableHeader>
              <TableRow>
                {COLUMNS.map((c) => (
                  <TableCell
                    key={c.property}
                    scope="col"
                    border="bottom"
                    align={c.align}
                  >
                    <Text>{c.label}</Text>
                  </TableCell>
                ))}
              </TableRow>
            </TableHeader>
            <TableBody>
              {data.tasks.map((task) => (
                <TableRow key={task.id}>
                  {COLUMNS.map((c) => (
                    <TableCell
                      scope="col"
                      border="bottom"
                      key={c.property}
                      scope={c.dataScope}
                      align={c.align}
                    >
                      <Text>
                        {c.format ? c.format(task) : task[c.property]}
                      </Text>
                    </TableCell>
                  ))}
                  <Button
                    plain={false}
                    icon={<Close size="small" />}
                    color="#3D138D"
                    margin="xsmall"
                    size="xsmall"
                    onClick={() => deleteInfo(book.id)}
                  />
                </TableRow>
              ))}
            </TableBody>
            <TableFooter>
              <TableRow>
                {COLUMNS.map((c) => (
                  <TableCell
                    key={c.property}
                    border="top"
                    align={c.align}
                  ></TableCell>
                ))}
              </TableRow>
            </TableFooter>
          </Table>
        </Box>
      </Grid>
    </Grommet>
  );
}

