import { React, useState, Component } from "react";
import { useQuery, useMutation } from "@apollo/client";
import { DELETE_TASK, TASK } from "./Query";
import {
  Table,
  TableBody,
  TableCell,
  TableFooter,
  TableHeader,
  TableRow,
  Text,
  Grommet,
  Button,
  Box,
  CheckBox,
} from "grommet";
import { Trash } from "grommet-icons";

const COLUMNS = [
  {
    property: "task",
    label: "Task",
    format: (task) => <strong>{task.task}</strong>,
  },
  {
    property: "limit",
    label: "Limit",
  },
];

function Todolist() {
  const { loading, error, data } = useQuery(TASK);
  const [checked, setChecked] = useState(false);

  const [deleteTask] = useMutation(DELETE_TASK, {
    refetchQueries: [{ query: TASK }],
  });

  const deleteTasks = (id) => {
    deleteTask({ variables: { id } });
  };

  const handleInputChange = () => {
    setChecked(true);
  };

  const content = checked == true ? React.createElement("p", `Hello `) : null;

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;

  return (
    <Grommet>
      <Box fill align="center" justify="center">
        <Box
          overflow={{ vertical: "scroll", horizontal: "auto" }}
          height="medium"
        >
          <Table sortable="true">
            <TableHeader>
              <TableRow>
                {COLUMNS.map((c) => (
                  <TableCell
                    key={c.property}
                    scope="col"
                    border="bottom"
                    align={c.align}
                  >
                    <Text>{c.label}</Text>
                  </TableCell>
                ))}
              </TableRow>
            </TableHeader>
            <TableBody>
              {data.tasks.map((task) => (
                <TableRow key={task.id}>
                  {COLUMNS.map((c) => (
                    <TableCell
                      scope="col"
                      border="bottom"
                      key={c.property}
                      align={c.align}
                    >
                      <Text>
                        {c.format ? c.format(task) : task[c.property]}
                      </Text>
                    </TableCell>
                  ))}
                  <TableCell>
                    <CheckBox checked={checked} onChange={handleInputChange} />
                    {content}
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </Box>
      </Box>
    </Grommet>
  );
}

export default Todolist;
