import React, { useState } from "react";
import { useQuery, useMutation } from "@apollo/client";
import { EDIT_TASK_COMPLETED, TASK, DELETE_TASK, PART_TASK } from "./Query";
import {
  Table,
  TableBody,
  TableCell,
  TableFooter,
  TableHeader,
  TableRow,
  Text,
  Grommet,
  Button,
  Box,
  CheckBox,
} from "grommet";
import { Trash } from "grommet-icons";

const COLUMNS = [
  {
    property: "task",
    label: "Task",
    format: (task) => <strong>{task.task}</strong>,
  },
  {
    property: "limit",
    label: "Limit",
  },
];

function Todolist({ checked: checkedProp }) {
  const { loading, error, data } = useQuery(TASK);
  //const [checked, setChecked] = useState(!!checkedProp);
  const [checked, setChecked] = useState(false);
  // const onChange = (event) => setChecked(event.target.checked);

  ///
  const [updateTaskCompleted] = useMutation(EDIT_TASK_COMPLETED, {
    refetchQueries: [{ query: TASK }],
  });

  ///

  const onChange = (id, event) => {
    updateTaskCompleted({ variables: { id, completed: true } });
    checked == false ? setChecked(true) : setChecked(false);

    //setChecked(event.target.checked);
  }; /////

  /* /////
  function changeCheckbox(idname){
    var obj = document.getElementById(idname);
    obj.checked = !obj.checked;
  }
  */ const deletebutton =
    checked == true ? (
      <Button plain={false} icon={<Trash size="small" />} margin="small" />
    ) : null;

  const [deleteTask] = useMutation(DELETE_TASK, {
    refetchQueries: [{ query: TASK }],
  });

  const deleteTasks = (id) => {
    deleteTask({ variables: { id } });
  };

  /*
  const deletebutton =
    checked == true ? (
      <Button
        plain={false}
        icon={<Trash size="small" />}
        margin="small"
        onClick={() => deleteTasks(task.id)}
      />
    ) : null;
*/

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;

  return (
    <Grommet>
      <Box fill align="center" justify="center">
        <Box
          overflow={{ vertical: "scroll", horizontal: "auto" }}
          height="medium"
        >
          <Table sortable="true">
            <TableHeader>
              <TableRow>
                {COLUMNS.map((c) => (
                  <TableCell
                    key={c.property}
                    scope="col"
                    border="bottom"
                    align={c.align}
                  >
                    <Text>{c.label}</Text>
                  </TableCell>
                ))}
              </TableRow>
            </TableHeader>
            <TableBody>
              {data.tasks.map((task) => (
                <TableRow key={task.id}>
                  {COLUMNS.map((c) => (
                    <TableCell
                      scope="col"
                      border="bottom"
                      key={c.property}
                      align={c.align}
                    >
                      <Text>
                        {c.format ? c.format(task) : task[c.property]}
                      </Text>
                    </TableCell>
                  ))}
                  <TableCell>
                    <CheckBox
                      checked={checked}
                      onClick={() => onChange(task.id)}
                    />
                  </TableCell>
                  <TableCell>{deletebutton}</TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </Box>
      </Box>
    </Grommet>
  );
}

export default Todolist;
