import React from 'react';
import { gql, useMutation } from '@apollo/client';

const Create_Bookinfo = gql`
  mutation CreateBookinfo($title: String!) {
    createBookinfo(title: $title ) {
      bookinfos {
        id
        title
     }
    }
`;


function CreateBookinfo (){
    let input;
    const [createBookinfo, { data }] = useMutation(Create_Bookinfo);

    return (
      <div>
        <form
          onSubmit={e => {
            e.preventDefault();
            createBookinfo({ variables: { title: input.value } });
            input.value = '';
          }}
        >
          <input
            ref={node => {
              input = node;
            }}
          />
          <button type="submit">Add Todo</button>
        </form>
      </div>
    );
  }

  export default CreateBookinfo;
