import React, { useState } from "react";
import { Box, Grommet, CheckBox, Button } from "grommet";
import { EDIT_TASK_COMPLETED, TASK, DELETE_TASK, PART_TASK } from "./Query";
import { useQuery } from "@apollo/client";
import { Trash } from "grommet-icons";

function Todotest() {
  const { loading, error, data } = useQuery(TASK);
  const [checked, setChecked] = useState(true);

  const onChange = (e) => {
    setChecked(e.target.checked);
    console.log(checked);
  };

  const deletebutton =
    checked === true ? (
      <Button plain={false} icon={<Trash size="small" />} margin="small" />
    ) : null;

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;

  return (
    <Grommet>
      <Box>
        {data.tasks.map((task) => (
          <CheckBox
            key={task.id}
            checked={checked}
            label={task.task}
            onChange={(id) => onChange()}
          />
        ))}
      </Box>
      <Box>{deletebutton}</Box>
    </Grommet>
  );
}

export default Todotest;
