import { gql } from '@apollo/client';



// ALLBOOKINFO //
export const BOOKINFO = gql`
{
bookinfos {
  id
  title 
  author
}
}
`
// CREATE NEW BOOKINFO //
export const CREATE_BOOKINFO = gql `
mutation CreateBookinfo($title: String!, $author: String!){
  createBookinfo(
    input:{
      title: $title
      author: $author
    }){
    bookinfo {
      id
      title
      author
    }
  }
}
`

// EDIT BOOKINFO //
export const EDIT_BOOKINFO = gql `
mutation UpdateBookinfo($id: ID!, $title: String!, $author: String!){
  updateBookinfo(
    input:{
      id: $id
      title: $title
      author: $author
    }){
    bookinfo {
      id
      title
      author
    }
  }
}
`
// DELETE BOOKINFO //
export const DELETE_BOOKINFO = gql `
mutation DeleteBookinfo($id: ID!){
  deleteBookinfo(
    input:{
      id: $id
    }){
    bookinfo {
      id
      title
      author
    }
  }
}
`
